sys_variable_value.value:
(function execute(inputs, outputs) {

  outputs.tla_enabled = gs.getProperty('glide.authenticate.enable.time_limited_authentication') === 'true';
  outputs.sso_enabled = gs.getProperty('glide.authenticate.multisso.enabled') === 'true';
  outputs.acr_enabled = gs.getProperty('glide.sso.acr.enabled') === 'true';

})(inputs, outputs);

