sys_variable_value.value:
(function execute(inputs, outputs) {

  const instanceURL = 'https://' + inputs.instanceName + '.service-now.com';
  const encodedInstanceURL = encodeURIComponent(instanceURL);
  let tla = new global.TimeLimitedAuthentication();
  let nonceObj = JSON.parse(tla.generateNonce(inputs.user, inputs.tlaConfig));
  outputs.tla_login_url = instanceURL + '/login_with_sso.do?uri=' + encodedInstanceURL + '&' + nonceObj.query_parameter;

})(inputs, outputs);

